CREATE TABLE author (
    id SERIAL NOT NULL PRIMARY KEY,
    name VARCHAR
);

CREATE TABLE post (
    id SERIAL NOT NULL PRIMARY KEY,
    title VARCHAR(70), -- 70 for SEO
    slug VARCHAR(70),
    body VARCHAR,
    author_id INTEGER REFERENCES author (id),
    post_date DATE
);

CREATE TABLE comment (
    id SERIAL NOT NULL PRIMARY KEY,
    comment VARCHAR,
    post_id INTEGER REFERENCES post (id),
    comment_post_datetime TIMESTAMP
);

